type capAmsConfig = {
    autoCompile: boolean; // generation DCL from CDS whenever CDS model changes
    authPushDcl: boolean; // if authPushDcl and autoCompile = true, pushes DCL bundle to AMS server on changes when app is started in hybrid mode via cds watch with binding to IAS
    /** @deprecated in favor of dclRoot */
    dclFolder: string; // outputfolder for CDS->DCL generation, input folder for DCL->DCN compilation
    dclRoot: string; // outputfolder for CDS->DCL generation, input folder for DCL->DCN compilation
    dcnRoot: string; // folder for DCN bundle compilation results during development
    /** @deprecated in favor of customDclRoot */
    customDclFolder: string; // folder for custom DCL policies that will automatically be combined with generated DCL policies during build or DCL generation
    customDclRoot: string; // folder for custom DCL policies that will automatically be combined with generated DCL policies during build or DCL generation
    cache: { // user role cache
        TTL: number; // maximum life-time in ms for cache entries
        limit: number; // max cached entries
    }
    /** @deprecated */
    opa: {
        inputFolder: string; // output folder for DCL->REGO compilation and policyAssignments file
        port: number;
    }
}

export = capAmsConfig;
